int main() {
  const int N = 8;
  int a[N], j, i;
  //逐个输入数组a元素
  for (j = 0; j &lt; N - 1; j++)        // N个元素共比较(N-1)轮
    for (i = 0; i &lt; N - 1 - j; i++)  //第j轮比较(N-1-j)次
      if (a[i] &gt; a[i + 1]) {         //交换
        int t = a[i];
        a[i] = a[i + 1];
        a[i + 1] = t;
      }
  //输出排序后的数组a
  return 0;
}
</code></pre>

