return 0;
}
long factorial(long number, int space) {
int i;
long result;
if (number &lt;= 1) {
for (i = 1; i &lt;= space; i++) {
printf(" ");
}
printf("return 1\n");
return 1;
} else {
for (i = 1; i &lt;= space; i++) {
printf(" ");
}
printf("number = %ld ,factorial( %ld , %d)\n", number, number - 1, space);
result = number * factorial(number - 1, space + 4);
for (i = 1; i &lt;= space; i++) printf(" ");
printf("return %ld\n", result);
return result;
}
}
</code></pre> <p>使用递归的例子：Fibonacci数列</p> <ul> <li>Fibonacci数列 : 0, 1, 1, 2, 3, 5, 8, 13, 21, …</li> </ul> C++<pre><code>#include &lt;stdio.h&gt;
long fibonacci(long number);
int main(int argc, char const *argv[]) {
long result;
long number;
printf("Enter an integer: ");
scanf("%ld", &amp;number);
result = fibonacci(number);
printf("Fibonacci( %ld ) =  %ld\n", number, result);
return 0;
}
long fibonacci(long n) {
if (n == 0 || n == 1) {
return n;
} else {
return fibonacci(n - 1) + fibonacci(n - 2);
}
